from __future__ import absolute_import 
import magnumfe.cpp as cpp
from dolfin import Matrix, Vector

__all__ = ["DofAssembler",
           "ScalarProductMatrix",
           "ScalarProductMatrix2",
           "TransScalarProductMatrix",
           "NormalizedVector"]


class DofAssembler(cpp.DofAssembler):
  """
  This class defines static methods for the assembly of _DofForm_ instances.
  """

  @staticmethod
  def assemble(a):
    """
    Assemles a _DofForm_.

    *Arguments*
        a
            _DofForm_ to assemble.
    """
    A = 0
    if a.rank() == 2:
      A = Matrix()
    elif a.rank() == 1:
      A = Vector()

    cpp.DofAssembler.assemble(A, a, True)
    return A

class ScalarProductMatrix(cpp.ScalarProductMatrix):
  """
  The class represents a matrix that is used to compute the
  nodewise scalar product with a given vector.
  """
  pass

class ScalarProductMatrix2(cpp.ScalarProductMatrix2):
  """
  The class represents a matrix that is used to compute the
  nodewise scalar product with a given vector.
  """
  pass

class TransScalarProductMatrix(cpp.TransScalarProductMatrix):
  """
  The class represents a transposed matrix that is used to
  compute the nodewise scalar product with a given vector.
  """
  pass

class NormalizedVector(cpp.NormalizedVector):
  """
  The class defines a _DofForm_ that represents a nodewise
  normalized vector field.
  """
  pass
