from __future__ import absolute_import 

from dolfin import *
from magnumfe.llg_terms import LLGTerm
from magnumfe.constants import Constants

__all__ = ["SpinCurrent"]

class SpinCurrent(LLGTerm):
  """
  This class represents the coupling with a spin polarized current.
  """

  def __init__(self):
    pass

  def form_term_rhs(self, state, w):
    return Constant(Constants.gamma / Constants.mu0) * state.material.c * inner(state.s, w)
