from __future__ import absolute_import 

__all__ = ["Material"]

class Material:
  def __init__(self, **kwargs):
    self.Aex   = kwargs.pop('Aex', 0)
    self.alpha = kwargs.pop('alpha', 0)
    self.gamma = kwargs.pop('gamma', 0)
    self.ms    = kwargs.pop('ms', 0)
    pass

  @staticmethod
  def py():
    return Material(alpha=0.1, Aex=13e-12, gamma=2.211e5, ms=8e5)
